package uz.salimovdeveloper.musicplayer.adaptersimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport uz.salimovdeveloper.musicplayer.databinding.ItemRecycBindingclass RvAdapter(val list:ArrayList<Music>, val rvClick: RvClick): RecyclerView.Adapter<RvAdapter.Vh>() {    inner class Vh(val rvItem: ItemRecycBinding): RecyclerView.ViewHolder(rvItem.root){        fun onBind(label: Music){            rvItem.itemMusicName.text=label.title            rvItem.itemMusicAbout.text=label.author            rvItem.mainCard.setOnClickListener {                rvClick.onClick(label)            }        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): Vh {        return Vh(ItemRecycBinding.inflate(LayoutInflater.from(parent.context), parent, false))    }    override fun onBindViewHolder(holder: Vh, position: Int) {        holder.onBind(list[position])    }    override fun getItemCount(): Int {        return list.size    }    interface RvClick{        fun onClick(label: Music)    }}